 onClick={() =>
 handlers.addItem({
 uuidv4,
 listState,
 setListState,
 inputValueState,
 setInputValueState,
 })
 }
 >
 Add
 </button>
 {inputValueState ? (
 <button
 className='clearInputButton'
 onClick={() => handlers.clearInput({ setInputValueState })}
 >
 Clear
 </button>
 ) : null}
 </div>
 <div className='listWrapper'>{renderedList}</div>
 </div>
 )
}
